import React, { useState, useRef, useEffect } from 'react';
import './ChipComponents.css';
import Chip from './CustomChips.tsx';

interface User {
  name: string;
  profilePicture: string;
  email: string;
}

interface AppProps {}

const App: React.FC<AppProps> = () => {
  const [input, setInput] = useState<string>('');
  const [chips, setChips] = useState<User[]>([]);
  const [showUserList, setShowUserList] = useState<boolean>(false);
  const [selectedItem, setSelectedItem] = useState<number | null>(null);

  const inputRef = useRef<HTMLInputElement>(null);
  const userListRef = useRef<HTMLUListElement>(null);

  const users: { name: string; profilePicture: string; email: string }[] = [
    { name: 'Shivkant Rai', profilePicture: 'data:image/jpeg;base64,', email: 'tim@example.com' },
    { name: 'Tracy Bing', profilePicture: 'data:image/jpeg;base64,', email: 'tracy@example.com' },
    { name: 'Bradley Wells', profilePicture: 'data:image/jpeg;base64,', email: 'bradley@example.com' },
    { name: 'Tim Cook', profilePicture: 'data:image/jpeg;base64,', email: 'timcook@example.com' } , 
    { name: 'Tim Hall', profilePicture: 'data:image/jpeg;base64,', email: 'tim@example.com' }
    // Add more users as needed
  ];

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
    setShowUserList(true);
    setSelectedItem(null);
  };

  const handleInputFocus = () => {
    if (input.trim() === '') {
      setShowUserList(true);
    }
  };

  const handleAddChip = (user: User) => {
    setChips([...chips, user]);
    setInput('');
    setShowUserList(false);
  };

  const handleRemoveChip = (chip: User) => {
    setChips(chips.filter(c => c.name !== chip.name));
  };

  const handleBackspace = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Backspace' && !input && chips.length > 0) {
      const lastChip = chips[chips.length - 1];
      handleRemoveChip(lastChip);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLUListElement>) => {
    const { key } = e;

    if (key === 'ArrowUp' || key === 'ArrowDown') {
      e.preventDefault();
      const listItems = Array.from(e.currentTarget.children);
      const currentIndex = selectedItem !== null ? selectedItem : -1;

      if (key === 'ArrowUp' && currentIndex > 0) {
        setSelectedItem(currentIndex - 1);
      } else if (key === 'ArrowDown' && currentIndex < listItems.length - 1) {
        setSelectedItem(currentIndex + 1);
      }
    } else if (key === 'Enter' && selectedItem !== null) {
      const selectedUser = users[selectedItem];
      handleAddChip(selectedUser);
    }
  };

  useEffect(() => {
    if (selectedItem !== null && userListRef.current) {
      const selectedListItem = userListRef.current.children[selectedItem] as HTMLElement;
      if (selectedListItem) {
        selectedListItem.scrollIntoView({ block: 'nearest' });
      }
    }
  }, [selectedItem]);

  const handleOutsideClick = (e: MouseEvent) => {
    const target = e.target as HTMLElement;
    if (inputRef.current && !inputRef.current.contains(target) && userListRef.current && !userListRef.current.contains(target)) {
      setShowUserList(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleOutsideClick);

    return () => {
      document.removeEventListener('mousedown', handleOutsideClick);
    };
  }, []);

  return (
    <div className="app">
      <h1 className="neon-heading">Pick Users</h1>
      <div className="chip-container">
        {chips.map(chip => (
          <Chip
            key={chip.name}
            label={chip.name}
            onDelete={() => handleRemoveChip(chip)}
            profilePicture={chip.profilePicture}
            selected
          />
        ))}
      </div>
      <input
        ref={inputRef}
        type="text"
        value={input}
        onChange={handleInputChange}
        onFocus={handleInputFocus}
        onKeyDown={handleBackspace}
        placeholder="Add new user..."
      />
      {showUserList && (
        <ul ref={userListRef} className="user-list" onKeyDown={handleKeyDown} tabIndex={0}>
          {users
            .filter(
              user =>
                user.name.toLowerCase().includes(input.toLowerCase()) &&
                !chips.some(c => c.name === user.name)
            )
            .map((user, index) => (
              <li key={user.name} onClick={() => handleAddChip(user)} className={index === selectedItem ? 'selected' : ''}>
                <Chip
                  label={user.name}
                  profilePicture={user.profilePicture}
                  email={user.email}
                  selected={false}
                />
              </li>
            ))}
        </ul>
      )}
    </div>
  );
};

export default App;
